using System;public struct Heroi {    public string Nome;    public string Poder;    public int Pontos;}class Program {    static Heroi[] herois = new Heroi[5];    static Heroi[] equipe = new Heroi[3];    static int heroisCadastrados = 0;    static void cadastrarHeroi() {        if (heroisCadastrados >= 5) {            Console.WriteLine("Você já cadastrou o máximo de 5 heróis.");            return;        }        Console.Write($"Digite o nome do {heroisCadastrados + 1}º herói: ");        herois[heroisCadastrados].Nome = Console.ReadLine();        Console.Write("Digite o super poder: ");        herois[heroisCadastrados].Poder = Console.ReadLine();        Console.Write("Digite a quantidade de pontos: ");        herois[heroisCadastrados].Pontos = int.Parse(Console.ReadLine());        heroisCadastrados++;    }    static void selecionarEquipe() {        if (heroisCadastrados == 0) {            Console.WriteLine("Nenhum herói cadastrado. Cadastre heróis primeiro.");            return;        }        Console.WriteLine("Selecione 3 heróis para criar sua equipe:");        for (int i = 0; i < heroisCadastrados; i++) {            Console.WriteLine($"({i + 1}) {herois[i].Nome} - Poder: {herois[i].Poder} - Pontos: {herois[i].Pontos}");        }        for (int i = 0; i < 3; i++) {            Console.WriteLine($"Digite o número do {i + 1}º membro:");            int escolha = int.Parse(Console.ReadLine());            equipe[i] = herois[escolha - 1];        }    }    static int calcularPontuacaoTotal() {        int pontosTotais = 0;        foreach (var heroi in equipe) {            pontosTotais += heroi.Pontos;        }        return pontosTotais;    }    static void exibirEquipe() {        Console.WriteLine("Sua equipe é formada por:");        foreach (var heroi in equipe) {            Console.WriteLine($"{heroi.Nome} com o poder {heroi.Poder} e {heroi.Pontos} pontos.");        }        int pontuacaoTotal = calcularPontuacaoTotal();        Console.WriteLine($"A pontuação total da equipe é {pontuacaoTotal}.");    }    static void menuPrincipal() {        int continuar = 0;        do {            Console.WriteLine("(1) Cadastro de Heróis");            Console.WriteLine("(2) Seleção de Equipe");            Console.WriteLine("(3) Exibição da Equipe");            Console.WriteLine("(4) Sair");            int escolha = int.Parse(Console.ReadLine());            switch (escolha) {                case 1:                    cadastrarHeroi();                    break;                case 2:                    selecionarEquipe();                    break;                case 3:                    exibirEquipe();                    break;                case 4:                    Console.WriteLine("Obrigado!");                    continuar = 1;                    break;                default:                    Console.WriteLine("Opção inválida.");                    break;            }        } while (continuar == 0);    }    static void Main() {        Console.WriteLine("Bem Vindo ao criador de equipes MARVEL");        Console.WriteLine("Cadastre até 5 heróis e forme uma equipe com no máximo 3 deles!");        menuPrincipal();    }}